mapper.select:
SELECT a.id id, a.recycleName recycleName, a.integral integral, a.appointmentTime appointmentTime, a.message message, a.status status, a.recycleImg recycleImg, a.createTime createTime, b.userName `name`, b.address addressName, b.mobile mobile, c.recycleCategoryName categoryName FROM `recycle` a LEFT JOIN address b ON a.addressId = b.id LEFT JOIN recyclecategory c ON a.recycleCategoryId = c.id WHERE 1=1
AND a.id=#{id}
AND a.status=#{status}
ORDER BY a.createTime DESC
